import React from 'react';
import randomuser from '../api/randomuser';
import Avatar from './Avatar';
import AvatarList from './AvatarList';
import Input from './Input';
import Button from './Button';

// import '../css/app.css';

class App extends React.Component {
  
  state = {avatarList:[]};

  componentDidMount(){
    this.getUser();
  }
  
  onSearchSubmit = async term => {
    // const response = await unsplash.get('/search/photos', {
      // params: { query: term }
    // });

    // this.setState({ images: response.data.results });
    console.log("search clicked");
  };
  async getUser(){
    const response = await randomuser.get();
    const user = response.data.results[0];
    this.setState({name:`${user.name.first} ${user.name.last}`, image:user.picture.large});

  }
  render(){
    return (
      <div className="ui container" style={{ marginTop: '10px' }}>
        <Input onSubmit={this.onSearchSubmit} />
        <AvatarList images={this.state.avatarList} />
      </div>
    );
  }
}

export default App;
