import React from 'react';
// import axios from 'axios';

import mockapi from '../api/mockapi';
import ContactList from './ContactList';
import Input from './Input';
import Button from './Button';
import ContactForm from './ContactForm';

// import '../css/app.css';

class App extends React.Component {
  
  state = {isAddNewContact:false,isEditContact:false,contactList:[],subName:"",filteredResult:[]};

  componentDidMount(){
    this.getContacts();
  }
  
  // onSearchParent = (newSearch) => {
  //   this.setState({subName:newSearch},
  //     () => {
  //       const filteredSearch = this.state.avatarList.filter(
  //         avatar => {
  //           return (avatar.name.toLowerCase().includes(this.state.subName.toLowerCase()));
            
  //         }
  //       )
  //       this.setState({filteredResult:filteredSearch});
  //     }
  //     )
 
  // };
  
  onCancelClick = ()=>{
    this.setState({isAddNewContact:false,isEditContact:false});
  }
  // add contact in state
  addNewContact(newContact){
    // contactList
  }
  deleteContact(contactId){
    const filteredContactList = this.state.contactList.filter(contact => contact.id !== contactId)
    this.setState({contactList:filteredContactList});
  }
  onAddNewContact = async (newContact)=>{
    this.setState({isAddNewContact:true});
    console.log("adding new contact");

    const contact = {
      name: 'siham egb',
      phone: '444444444',
      email: 'siham.egb@gmail.com',
      image: 'https://www.google.com/search?q=apple+image&tbm=isch&source=iu&ictx=1&fir=4RZskSm2_JRZpM%252CcI9MDLcYnx0VhM%252C_&vet=1&usg=AI4_-kSAONRh1oOQRWRQUlzPiBhJ8X3xyg&sa=X&ved=2ahUKEwiG8t_k2_nrAhXJ5-AKHY4kA4sQ9QF6BAgKEEo#imgrc=4RZskSm2_JRZpM',
      // name: this.state.name
    };

    const response = await mockapi.post(`contacts/${contact}`);
    this.setState({ contactList: this.contactList.push(response.data)});
    console.log("response on add",response);

  }
   onDeleteContact = async (id) => {
    console.log("deleting contact",id);
    const response = await mockapi.delete(`contacts/${id}`);
    console.log("response on delete",response);
    this.deleteContact(id);
  }
  onEditContact = (id) => {
    this.setState({isEditContact:true});
    console.log("editing contact");
  }

  // https://randomuser.me/api/?results=10
  async getContacts(){
    
    const response = await mockapi.get('contacts');
   
    console.log("response is",response.data);
    this.setState({ contactList: response.data});
    // this.setState({name:`${user.name.first} ${user.name.last}`, image:user.picture.large});
  }
  renderContent(){
    if(this.state.isAddNewContact)
    {
      return <ContactForm
              positiveButton = "Add"
              negativeButton = "Cancel"
              onNegativeClick={this.onCancelClick}
              onPositiveClick={this.onAddNewContact}
              ></ContactForm>;
    }
    if(this.state.isEditContact)
    {
      return <ContactForm
              // contact = 
              positiveButton = "Save"
              negativeButton = "Cancel"
              onNegativeClick={this.onCancelClick}
              onPositiveClick={this.onEditContact}
              onEditClick={() => {this.setState({isEditContact:true})}}
              ></ContactForm>;
    }
    return (
      <div>
        <Button text="New Contact" onButtonClick={() => {this.setState({isAddNewContact:true})}}></Button>
        <ContactList 
          contacts={this.state.contactList} 
          onDeleteContact = {this.onDeleteContact}
          onEditContact = {this.onEditContact}
        />
      </div>
    );

  }
  render(){
    return this.renderContent();
  }
}

export default App;
