import React from 'react';
import chucknorris from '../api/chucknorris.js';
import Joke from './Joke';

class App extends React.Component {
  
  state = {joke:null,icon:null};

  
  async onGetJoke(){
    const response = await chucknorris.get(
      '/jokes/random',{
        // params:{query:term}
      }
    );
    console.log(response.data.value);
    this.setState({joke:response.data.value, icon:response.data.icon

      
    });
  }
   
  
  render(){
    return (
      <div>
        <button onClick={() => this.onGetJoke()}>Get a Joke</button>
        <Joke text={this.state.joke} icon={this.state.icon}></Joke>
      </div>
    );
  }
}

export default App;
