import React from 'react';

class Joke extends React.Component {
	
	constructor(props){
		super(props);
	}
  // state = {joke:null};
 
  
  render(){
    return (
      <div className="ui raisedcard">
				<div className="content">
        	<div className="header">{this.props.text}</div>
				</div>
				<div className="extra content">
    			<div className="right floated author"><img className="ui avatar image" src={this.props.icon}/></div>
				</div>
			</div>
      );
  }
}

export default Joke;
