import React from 'react';
import chucknorris from '../api/chucknorris.js';
import Joke from './Joke';
import Category from './Category';

import '../css/app.css';

class App extends React.Component {
  
  state = {joke:null,categories : []};

  async getCategories(){
    const response = await chucknorris.get(
      '/jokes/categories'
    );
    this.setState({categories:response.data});
  }
  componentDidMount(){
    this.getCategories();
  }
  
  getCategoriesNames(){
    return this.state.categories.map(category => {
      return <Category 
        text={category}
        ></Category>;
    });
  }
  async onGetJoke(){
    const response = await chucknorris.get(
      '/jokes/random',{
        // params:{query:term}
      }
    );
    this.setState({joke:response.data.value, icon:response.data.icon_url
      
    });
  }
   
  
  render(){
    return (
      <div>
        <img src="https://w0.pngwave.com/png/654/401/word-academy-answers-for-word-academy-4-pics-1-word-android-video-game-chuck-norris-png-clip-art.png"></img>
        <button className="ui button primary" onClick={() => this.onGetJoke()}>Get Random Joke</button>
        <div>
          <div>Categories</div>
            <div>
            {this.getCategoriesNames()}
            </div>
        </div>
        <Joke text={this.state.joke} icon={this.state.icon}></Joke>

      
      </div>
    );
  }
}

export default App;
