import React from 'react';
import randomuser from '../api/randomuser';
import Avatar from './Avatar';
import AvatarList from './AvatarList';
import Input from './Input';
import Button from './Button';

// import '../css/app.css';

class App extends React.Component {
  
  state = {avatarList:[]};

  componentDidMount(){

    this.getUsers(10);
  }
  
  onSearchSubmit = async term => {
    // const response = await unsplash.get('/search/photos', {
      // params: { query: term }
    // });

    // this.setState({ images: response.data.results });
    console.log("search clicked");
  };
  
  // https://randomuser.me/api/?results=10
  async getUsers(num){
    const response = await randomuser.get(
        {
        params:{results:num}
      });
  
    for(let i=0;i<num;i++)
    {
        const user = response.data.results[i];
        const customUser = {
          name: `{${user.name.first} ${user.name.last}}`,
          image: `{${user.picture.large}}`,
        }
        this.setState({ avatarList: avatarList.push(customUser) });
    }
    // this.setState({name:`${user.name.first} ${user.name.last}`, image:user.picture.large});
  }
  render(){
    return (
      <div className="ui container" style={{ marginTop: '10px' }}>
        <Input onSubmit={this.onSearchSubmit} />
        <AvatarList avatars={this.state.avatarList} />
      </div>
    );
  }
}

export default App;
