import React from 'react';
import chucknorris from '../api/chucknorris.js';
import Joke from './Joke';
import Category from './Category';
class App extends React.Component {
  
  state = {joke:null,icon:null};
  state = {categories : []};

  async getCategories(){
    const response = await chucknorris.get(
      '/jokes/categories'
    );
    console.log(response.data);
    this.setState({categories:response.data});

  }
  componentDidMount(){
    this.getCategories();

  }
  getCategories(){
    return this.state.categories.map(category => <div>category</div>);
  }
  async onGetJoke(){
    const response = await chucknorris.get(
      '/jokes/random',{
        // params:{query:term}
      }
    );
    console.log(response.data.value);
    this.setState({joke:response.data.value, icon:response.data.icon_url
      
    });
  }
   
  
  render(){
    return (
      <div className="ui">
        <button className="ui button primary" onClick={() => this.onGetJoke()}>Get a Joke</button>
        <Joke text={this.state.joke} icon={this.state.icon}></Joke>
        <div>
          <div className="ui header">Categories</div>
          <div>
          {this.getCategories()}
          </div>
        </div>

      
      </div>
    );
  }
}

export default App;
