import React from 'react';
import randomuser from '../api/randomuser';
import Avatar from './Avatar';
// import '../css/app.css';

class App extends React.Component {
  
  // state = {joke:null,icon:null};
  constructor(props){
    super(props);
    this.getUser();

  }
  async getUser(){
    const response = await randomuser.get();
    const user = response.data.results[0];
    this.setState({firstName:user.first+user.last, image:user.picture.large});

  }
  render(){
    return (
      <Avatar id="111" name={this.state.name} image={this.state.image}></Avatar>
    );
  }
}

export default App;
