import React from 'react';
import chucknorris from '../api/chucknorris.js';
import Joke from './Joke';
import Category from './Category';

import '../css/app.css';

class App extends React.Component {
  
  constructor(props){
    super(props);
    this.state = {joke:null,categories : [],logoUrl:""};
    this.handleClick = this.handleClick.bind(this);

  }

  async getCategories(){
    const response = await chucknorris.get(
      '/jokes/categories'
    );
    this.setState({categories:response.data});
  }
  async getIcon(){
    const response = await chucknorris.get(
      '/jokes/random',{
      }
    );
    this.setState({logoUrl:response.data.icon_url})
  }
  componentDidMount(){
    this.getCategories();
    this.getIcon();
  }
  
  renderCategories(){
    return this.state.categories.map(category => {
      return <Category 
        text={category}
        onChildClick={this.handleClick}
        key={category}
        ></Category>;
    });
  }
  async onGetJoke(){
    const response = await chucknorris.get(
      '/jokes/random',{
        // params:{query:term}
      }
    );
    this.setState({joke:response.data.value});
  }
  handleClick(childJoke){
    this.setState({joke:childJoke});
  }
   
  
  render(){
    return (
      <div>
        <h1> Chuck Norris Jokes</h1>
        <img src={this.state.logoUrl} alt="chuck norris logo"></img>
        <div>
          <button className="ui button primary" onClick={() => this.onGetJoke()}>Get Random Joke</button>
        </div>
        <Joke text={this.state.joke}></Joke>
        <div>
          <div className="categories">Categories</div>
           
            {this.renderCategories()}
            
        </div>

      
      </div>
    );
  }
}

export default App;
