import React from 'react';
import './Box.css';

class Box extends React.Component{
	constructor(props){
		super(props);
		this.state = {visible:true};
		console.log(props.size);
		// props.size 

		// this.state = {visible:true};
		// this.handleClick = this.handleClick.bind(this);
	}
	render() {
			return(
			<div className="ui container Counter">
			 	<div className="Box" style={{width: this.props.size , height:this.props.size}}></div>
			</div>
		);
	}
	// content visible in screen
	componentDidMount(){


	}
	// content update
	componentDidUpdate(){
		setTimeout(4000,() => {
			super.setState({visible:false});
			}
				)
		
	}

}


export default Box;
