import React from 'react';

let style = {width:'200px',height:'200px',backgroundColor:'yellow'};
class SillyBox extends React.Component{
	constructor(props){
		super(props);
		this.state = {visible:true};
		this.handleClick = this.handleClick.bind(this);
	}
	handleClick()  {
		this.state.visible ? this.setState({visible:false}) : this.setState({visible:true});
	}
	render() {
		
			if(this.state.visible){
				style.display = 'block';
			}
			else{
				style.display = 'hidden';
			}
			return(
			<div className="ui container Counter">
				<button class="ui button" onClick = {this.handleClick}> show/hide</button>		
			 <div style = {style}> </div>;
			</div>
	
		);
	}

}


export default SillyBox;
