import React from 'react';
import ReactDOM from 'react-dom';
// import './index.css';
import Spinner from './Spinner';


class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {timerMin: 3 , timerSec:0};
    this.myInterval = null;
  }
  decrementTimer(){
    if(this.state.timerMin === 0 && this.state.timerSec === 0)
    {
      clearInterval(this.myInterval);
      return;
    }
    if(this.state.timerSec === 0){
      this.setState(this.state.timerMin = this.state.timerMin-1); 
      this.setState(this.state.timerSec = 59); 
    }
    else{
      this.setState(this.state.timerSec = this.state.timerMin-1); 
    }
  }
  componentDidMount(){
    if(this.state.timer > 7)
    {
      this.myInterval = setInterval( () => { 
        this.decrementTimer();
      } , 1000)
    }
  }
  render(){
    return (
      <div>
        timer: {this.state.timer}
        <Spinner></Spinner>
      </div>
       );

  }
}

ReactDOM.render(<App />, document.querySelector('#root'));
