import React from 'react';
import './ColorBox.css';

const style = { backgroundColor : 'blue'};
class ColorBox extends React.Component{
		state = {counter:0 , color: `hsl(0, 100%, 50%)`};

	
	render() {
		console.log(this.state.color);
			return(
			<div className="color-box" style={{backgroundColor:this.state.color}} >
			</div>
			);
	}
	changeColor(){
		// console.log("changing color");
		this.setState({color :`hsl(${Math.random() * 360}, 100%, 50%)`});
	}
	// content visible in screen
	componentDidMount(){
		console.log("hello");

		setInterval(() => {
			this.changeColor();
		}, 500);
		
	}
	// content update
	componentDidUpdate(){
		this.setState({counter:this.state.counter++});
		console.log("changes are done");
	}

}


export default ColorBox;
