import React from 'react';

class Form extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {
			firstName: "",
      lastName: "",
      freeText: "",
      age: "",
      // isChecked:this.props.isChecked,
    };
	}
	
	handleChange = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };
	onFormClick = (event) =>{
		event.preventDefault();

    const formValues = [
      { title: "First Name", value: this.state.firstName },
      {
        title: "Last Name",
        value: this.state.lastName,
      },
      {
        title: "Age",
        value: this.state.age,
      },
      {
        title: "Free Text",
        value: this.state.freeText,
      },
    ];
    this.props.onSurveySubmit(formValues);
  };

  render(){
    return (
			<div className="form">
				<div className="ui">
					<label>First Name:</label> 
					<input></input>
				</div>	
				<div className="ui">
					<label>Last Name:</label> 
					<input></input>
				</div>
				<div className="ui">
          <label>Age:</label> 
					<select>
						<option value="group1">0-15</option>
						<option value="group2">16-59</option>
						<option value="group3">60-</option>
					</select>
				</div>
				<div className="ui">
          <label>Free Text:</label> 
					<input></input>
				</div>		
				<button onClick="onFormClick">Continue</button>
		
		</div>	
			);

  }
}

export default Form;
