import React from 'react';
import chucknorris from '../api/chucknorris.js';
import '../css/category.css';

class Category extends React.Component {
	
	constructor(props){
		super(props);
		this.state = {joke:null};
		// this.onCategoryClick = this.props.onChildClicked(this);
	}
	
	
  // state = {joke:null};
 
   onCategoryClick = async (e) => {
		// console.log("button clicked",e);
		console.log("button clicked",this.props.text);
		const response = await chucknorris.get(
      '/jokes/random',{
        // params:{query:term}
      }
    );
		this.props.onChildClick(response.data.value);
		console.log(response.data.value);
    // this.setState({joke:response.data.value, icon:response.data.icon_url
    // });
	}
  render(){
    return (
			<div
			onClick = {this.onCategoryClick}
			>
				<button className="ui button primary">
				{this.props.text}
				</button>
				{/* <Joke text={this.state.joke}></Joke>     */}
			</div>
      );
  }
}

export default Category;
