import React from 'react';



const styleImportant = {  color:'red',fontWeight: '900'};
let buttonStyle = {};


const Button = (props) => {
	if(props.buttonImportant === true)
	{
		buttonStyle = styleImportant;
	}
  return (
		<div className="button">
    	<button style = {buttonStyle}>{props.text}</button>
		</div>

  );
};

export default Button;
