import React from 'react';
import './index.css';

class SillyBox extends React.Component{
	constructor(props){
		super(props);
		this.myClassName = 'Box';
		this.state = {visible:true};
		this.handleClick = this.handleClick.bind(this);
	}
	handleClick()  {
		if(this.state.visible){
			this.setState({visible:false});
		}
		else{
			this.setState({visible:true});
		}
	}
	render() {
		
			if(this.state.visible){
				this.myClassName = 'Box';
			}
			else{
				this.myClassName = 'hiddenBox';
			}
			return(
			<div className="ui container Counter">
				<button className="ui button" onClick = {this.handleClick}> show/hide</button>		
			 	<div className={this.myClassName}></div>
			</div>
	
		);
	}

}


export default SillyBox;
