import React from 'react';
import randomuser from '../api/randomuser';
import Avatar from './Avatar';
import AvatarList from './AvatarList';
import Input from './Input';
import Button from './Button';

// import '../css/app.css';

class App extends React.Component {
  
  state = {avatarList:[]};

  componentDidMount(){
    this.getUser();
  }
  
  async getUser(){
    const response = await randomuser.get();
    const user = response.data.results[0];
    this.setState({name:`${user.name.first} ${user.name.last}`, image:user.picture.large});

  }
  render(){
    return (
      
      <Avatar id="111" name={this.state.name} image={this.state.image}></Avatar>
    );
  }
}

export default App;
