import React from 'react';
import axios from 'axios';

// import randomuser from '../api/randomuser';
import AvatarList from './AvatarList';
import Input from './Input';
import Button from './Button';

// import '../css/app.css';

class App extends React.Component {
  
  state = {avatarList:[],subName:"",filteredResult:[]};

  componentDidMount(){
    this.getUsers(10);
  }
  
  onSearchParent = (newSearch) => {
    this.setState({subName:newSearch},
      () => {
        const filteredSearch = this.state.avatarList.filter(
          avatar => {
            return (avatar.name.toLowerCase().includes(this.state.subName.toLowerCase()));
            
          }
        )
        this.setState({filteredResult:filteredSearch});
      }
      )
 
  };
  
  onButtonClick = ()=>{
    console.log("child button clicked");
    this.setState({avatarList:this.state.filteredResult});

  }
  // https://randomuser.me/api/?results=10
  async getUsers(num){
    
    const response = await axios.get(`https://randomuser.me/api/?results=${num}`);
    const temp = [];
    for(let i=0;i<num;i++)
    {
        const user = response.data.results[i];
        const customUser = {
          name: `${user.name.first} ${user.name.last}`,
          image: `${user.picture.large}`,
        }
        temp.push(customUser);
      }
    this.setState({ avatarList: temp,filteredResult:temp});
    console.log(response.data.results);
    // this.setState({name:`${user.name.first} ${user.name.last}`, image:user.picture.large});
  }
  render(){
    return (
      <div className="ui container" style={{ marginTop: '10px' }}>
        <Input onSearch={this.onSearchParent} />
        <Button text="filter" onButtonClick={this.onButtonClick}></Button>
        <AvatarList avatars={this.state.filteredResult} />
      </div>
    );
  }
}

export default App;
