import React from 'react';
import './ColorBox.css';

class ColorBox extends React.Component{
		state = {counter:0};
	
	render() {
		if(this.state.visible)
		{
			return(
			<div className="ui container Counter">
			 	<div className="Box" style={{width: this.props.size , height:this.props.size}}></div>
			</div>
		);
		}
		else{
			return null;
		}
	}
	changeColor(){
		console.log("changing color");
	}
	// content visible in screen
	componentDidMount(){
		
		setTimeout(500,this.changeColor);
	}
	// content update
	componentDidUpdate(){
		console.log("changes are done");
	}

}


export default ColorBox;
