import React from 'react';
import './ColorBox.css';

class ColorBox extends React.Component{
		state = {counter:0 , color: `hsl(0, 100%, 50%)` , shape:'square',toRemove:false};

	
	render() {
		// console.log(this.state.color);
			return(
			<div className={this.state.shape} style={{backgroundColor:this.state.color}} >
			</div>
			);
	}
	
	changeStyle(){
		// console.log("changing color",this.state.counter);
		if(this.state.counter === 5){
			this.setState({shape:'circle'});
		}
		if(this.state.counter <= 10)
		{
			this.setState({counter:this.state.counter+1 , color :`hsl(${Math.random() * 360}, 100%, 50%)`});
		}
	}
	// content visible in screen
	componentDidMount(){
		setInterval(() => {
			this.changeStyle();
		}, 500);
		
	}
	// content update
	componentDidUpdate(){
		console.log("changes are done");
	}

}


export default ColorBox;
