import React from 'react';
import './ColorBox.css';

class ColorBox extends React.Component{
		state = {counter:0 , color: `hsl(0, 100%, 50%)` , shape:'square'};

	
	render() {
		// console.log(this.state.color);
		if(this.state.shape === 'circle'){
			return(
			<div className="circle" style={{backgroundColor:this.state.color}} >
			</div>
			);
		}
			return(
			<div className="color-box" style={{backgroundColor:this.state.color}} >
			</div>
			);
	}
	
	changeColor(){
		console.log("changing color",this.state.counter);
		if(this.state.counter === 5){
			this.setState({shape:'circle'});
		}
		if(this.state.counter <= 10)
		{
			this.setState({counter:this.state.counter+1 , color :`hsl(${Math.random() * 360}, 100%, 50%)`});
		}
	}
	// content visible in screen
	componentDidMount(){
		console.log("hello");

		setInterval(() => {
			this.changeColor();
		}, 500);
		
	}
	// content update
	componentDidUpdate(){
		// console.log("changes are done");
	}

}


export default ColorBox;
