import React from 'react';
import './Box.css';

class Box extends React.Component{
	constructor(props){
		super(props);
		this.state = {visible:true , startX:'0px'};
		console.log(props.size);
		// props.size 

		// this.state = {visible:true};
		// this.handleClick = this.handleClick.bind(this);
	}
	render() {
		if(this.state.visible)
		{
			return(
			<div className="ui container Counter">
			 	<div className="Box" style={{width: this.props.size , height:this.props.size}}></div>
			</div>
		);
		}
		else{
			return null;
		}
	}

	// content visible in screen
	componentDidMount(){
		console.log("componentDidMount");
		setTimeout(1000,"after 1 sec");
	}
	// content update
	componentDidUpdate(){
		console.log("changes are done");
			}

}


export default Box;
