import React from 'react';
import './ColorBox.css';

const style = { backgroundColor : 'blue'};
class ColorBox extends React.Component{
		state = {counter:0 , color: `hsl(0, 100%, 50%)`};

	
	render() {
		// console.log(this.state.color);
		if(this.state.counter <= 10)
		{
			return(
			<div className="color-box" style={{backgroundColor:this.state.color}} >
			</div>
			);
		}
		else{
			return null;
		}
	}
	changeColor(){
		console.log("changing color",this.state.counter);
		if(this.state.counter <= 10)
		{
			this.setState({counter:this.state.counter+1 , color :`hsl(${Math.random() * 360}, 100%, 50%)`});
		}
	}
	// content visible in screen
	componentDidMount(){
		console.log("hello");

		setInterval(() => {
			this.changeColor();
		}, 500);
		
	}
	// content update
	componentDidUpdate(){
		// console.log("changes are done");
	}

}


export default ColorBox;
