import React from 'react';
import './Box.css';

class Box extends React.Component{
	constructor(props) {
    super(props);
    this.state = {
      shouldRender: false,
    };
  }

	componentDidMount() {
    setTimeout(() => {
      this.setState({ shouldRender: true });
    }, 1000);
  }

  render() {
   return null;
  }

}


export default Box;
