import React from 'react';



const styleImportant = {  color:'red',fontWeight: '900'};
// let buttonStyle = {};

const style = {color: 'red',display:'flex' , flexDirection:'column' , justifyContent:'center', margin:'auto', backgroundColor: 'blue', width: '700px' , height:'400px' };


const Button = (props) => {
	// if(props.buttonImportant === true)
	// {
	// 	buttonStyle = styleImportant;
	// }
  return (
		<div className="button" style={style}>
    	<button style = {styleImportant}>{props.text}</button>
		</div>

  );
};

export default Button;
