import React from 'react';
import mockapi from '../api/mockapi';
import Button from '../components/Button'

class ContactForm extends React.Component {
  
  state = {isAddNewContact:true,name:"", phone:"",email:"",image:""};
	
	handleChange = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState((prevState) => ({
      ...prevState,
      [name]: value,
    }));
	};
	handleSubmit = (event) => {
    event.preventDefault();

		const contact = {
      name: this.state.name,
      phone: this.state.phone,
      email: this.state.email,
      image: 'https://www.google.com/search?q=apple+image&tbm=isch&source=iu&ictx=1&fir=4RZskSm2_JRZpM%252CcI9MDLcYnx0VhM%252C_&vet=1&usg=AI4_-kSAONRh1oOQRWRQUlzPiBhJ8X3xyg&sa=X&ved=2ahUKEwiG8t_k2_nrAhXJ5-AKHY4kA4sQ9QF6BAgKEEo#imgrc=4RZskSm2_JRZpM',
		};
    this.props.onPositiveClick(contact);
	};
	handleCancel = (event) => {
		this.setState({isAddNewContact:false});
	}
	
	
  // onAddNewContact = ()=>{
  //   console.log("child button clicked");
	// 	this.addNewContact();
	// 	// this.setState({avatarList:this.state.filteredResult});

  // }
  // https://randomuser.me/api/?results=10
  // async addNewContact(){
    
  //   const response = await mockapi.get('contacts');
   
  //   console.log("response is",response.data);
  //   this.setState({ contactList: response.data});
  //   // this.setState({name:`${user.name.first} ${user.name.last}`, image:user.picture.large});
  // }
  render(){
    return (
			<form onSubmit={this.handleSubmit}>
			<div>
				<label>Name:</label>
				<input
					type="text"
					name="name"
					value={this.state.name}
					onChange={this.handleChange}
				/>
			</div>

			<div>
				<label>Phone:</label>
				<input
					type="text"
					name="phone"
					value={this.state.phone}
					onChange={this.handleChange}
				/>
			</div>

			<div>
				<label>Email:</label>
				<input
					type="text"
					name="email"
					value={this.state.email}
					onChange={this.handleChange}
				/>
			</div>
		
		
			<div>
				<Button
					text = {this.props.positiveButton}
					onButtonClick={this.props.onPositiveClick}
				></Button>
				<Button
					text = {this.props.negativeButton}
					onButtonClick={this.props.onNegativeClick}
				></Button>
			</div>
		</form>
    );
  }
}

export default ContactForm;
