import React from 'react';
import randomuser from './api/randomuser.js'

import '../css/app.css';

class App extends React.Component {
  
  state = {joke:null,icon:null};
  state = {categories : []};

  async getCategories(){
    const response = await tinyface.get(
      '/users'
    );
    console.log(response.data);
    this.setState({categories:response.data});
  }
  componentDidMount(){
    this.getCategories();
  }
  
  getCategoriesNames(){
    return this.state.categories.map(category => {
      return <Category 
        text={category}
        ></Category>;
    });
  }
  async onGetJoke(){
    const response = await chucknorris.get(
      '/jokes/random',{
        // params:{query:term}
      }
    );
    console.log(response.data.value);
    this.setState({joke:response.data.value, icon:response.data.icon_url
      
    });
  }
   
  
  render(){
    return (
      <div className="ui">
        {/* <button className="ui button primary" onClick={() => this.onGetJoke()}>Get a Joke</button> */}
        <Joke text={this.state.joke} icon={this.state.icon}></Joke>
        <div>
          <div className="ui header">Categories</div>
          <div>
          {this.getCategoriesNames()}
          </div>
        </div>

      
      </div>
    );
  }
}

export default App;
