import React from 'react';
import ReactDOM from 'react-dom';
// import './index.css';
import Spinner from './Spinner';
// import Timer from './Timer';


class App extends React.Component {
  constructor(props){
    super(props);    
    this.myInterval = null;

    this.state = {
      shouldRender: true,
      timerMin: 1 , 
      timerSec:0,
    };
  }

  decrementTimer(){
    if(this.state.timerMin === 0 && this.state.timerSec === 0)
    {
      clearInterval(this.myInterval);
      return;
    }
    if(this.state.timerSec === 0){
      this.setState({timerMin : this.state.timerMin-1}); 
      this.setState({timerSec : 59}); 
    }
    else{
      this.setState({timerSec : this.state.timerSec-1}); 
    }
  }

  componentDidMount() {
    this.myInterval = setInterval( () => { 
      this.decrementTimer();
    } , 1000)


  }

  render(){
    return this.state.shouldRender ? (
      <div>
        timer: {this.state.timerMin}:{this.state.timerSec}
      <Spinner></Spinner>
      </div>
    ) : null;

  }
}

ReactDOM.render(<App />, document.querySelector('#root'));
