import React from 'react';
import chucknorris from '../api/chucknorris.js';

class App extends React.Component {
  
  state = {joke:null};

  
  async onGetJoke(){
    const response = await chucknorris.get(
      '/jokes/random',{
        // params:{query:term}
      }
    );
    console.log(response.data.value);
    this.setState({joke:response.data.value});
  }
   
  
  render(){
    return (
      <div>
        <button onClick={() => this.onGetJoke()}>Get a Joke</button>
        <div>{this.state.joke}</div>
      </div>
    );
  }
}

export default App;
