import React from 'react';



const styleImportant = {  fontWeight: 'bold'};
let buttonStyle = {};

const Button = (props) => {
	if(props.buttonImportant === "true")
	{
		buttonStyle = styleImportant;
	}
  return (
		<div className="button" style={buttonStyle}>
    	<button>{props.text}</button>
		</div>

  );
};

export default Button;
