import React from 'react';
import ReactDOM from 'react-dom';
// import './index.css';
import Checkbox from './Checkbox';

class App extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {
      // shouldRemoveBox: false,
    };
  }
 
  render(){
    return (
			<form class="ui form">
				<Checkbox isChecked={true} text="I read the term of the app"></Checkbox>
				<Checkbox isChecked={false} text="I accept the term of the app"></Checkbox>
				<Checkbox isChecked={false} text="I want to get the weekly news letter"></Checkbox>
				<Checkbox isChecked={true} text="I want to get sales and offers"></Checkbox>
			</form>);

  }
}

export default App;
