import React from 'react';

import '../css/app.css';
import CustomButton from './CustomButton.js'
const colors = ["blue","red","yellow"];

class App extends React.Component {
	
	constructor(props){
		super(props);
		this.handleClick = this.handleClick.bind(this);
		this.state = {color:null};
	}

  handleClick(childColor){
		console.log(childColor);
		this.setState({color:childColor});
	}
	renderContent(){
		return colors.map( color => 
		<CustomButton 
			key = {color} 
			color={color}>
			callbackFromParent = {this.handleClick}
			</CustomButton>);
	}
  render(){
    return (
			<div>
				<div>
					{this.renderContent()}
				</div>
				<div>
					<h3>chosen color: {this.state.color}</h3>
				</div>

			</div>
      );
  }
}

export default App;
