import React from 'react';
import './ColorBox.css';

class ColorBox extends React.Component{
		state = {counter:0};
	
	render() {
			return(
			<div className="color-box">
			</div>
			);
	}
	changeColor(){
		console.log("changing color");
	}
	// content visible in screen
	componentDidMount(){
		console.log("hello");

		setTimeout(500,this.changeColor);
	}
	// content update
	componentDidUpdate(){
		console.log("changes are done");
	}

}


export default ColorBox;
